xwikidoc.content:
{{groovy}}
import java.util.List;

import javax.inject.Inject;
import javax.inject.Named;
import javax.inject.Singleton;

import org.xwiki.component.annotation.Component;
import org.xwiki.query.Query;
import org.xwiki.query.QueryException;
import org.xwiki.query.QueryManager;
import org.xwiki.script.service.ScriptService;

@Component
@Named("invoice")
@Singleton
public class InvoicesScriptService implements ScriptService
{
    @Inject
    private QueryManager queryManager;

    public long getNewNumber() throws QueryException
    {
        def query =
            this.queryManager.createQuery("select invoice.number "
                + "from Document doc, doc.object(InvoicesCode.InvoiceClass) as invoice "
                + "where doc.space = 'Invoices' AND invoice.number > 0"
                + "ORDER BY invoice.number DESC", Query.XWQL);
        query.setLimit(1);

        def invoices = query.execute();

        def newNumber;
        if (invoices.isEmpty()) {
            newNumber = 0;
        } else {
            newNumber = invoices.get(0);
        }

        return newNumber + 1;
    }
}
{{/groovy}}
