{{groovy}}
import org.xwiki.component.annotation.ComponentAnnotationLoader;

// Register a groovy script service, the listener is just used as entry pointpackage com.xpn.xwiki;

def componentManager = services.component.getComponentManager('wiki:' + services.wiki.currentWikiId)

def loader = new ComponentAnnotationLoader();
def descriptors = loader.getComponentsDescriptors(InvoicesScriptService.class)
for (descriptor in descriptors) {
    componentManager.registerComponent(descriptor);
}
{{/groovy}}

